import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import HomePage from './homepage';
import InfoPage from './infopage';
import {Layout, Header, Navigation, Drawer,Textfield, Content} from 'react-mdl';
import { Route, Switch } from "react-router-dom";
import Navbar from "../Landing/navbar";
import PrivateRoute from "../PrivateRoute/privateroute";


class Main extends Component {
  onLogoutClick = e => {
    e.preventDefault();
    this.props.logoutUser();
    window.location.href = "../login";

  };

  onSetAddress = e => {
    e.preventDefault();
    this.props.logoutUser();
    window.location.href = "../login";

  };

  render() {
    const { user } = this.props.auth;

    return (
      <div style={{height: '100vh', position: 'relative'}}>
        <Layout fixedHeader fixedDrawer>
          <Header className= "header-color" title="Covid-19 App" scroll>
            <Textfield
              value=""
              onChange={() => {}}
              label="Search"
              expandable
              expandableIcon="search"
            />
          </Header>
          <Drawer title="Title">
            <b>Hey there,</b> {user.username.split(" ")[0]}
            <Navigation>
              <a href="/dashboard/home">Home</a>
              <a href="/dashboard/infopage">Info</a>
              <form>
                <h5>Address</h5>
                <p1>
                  <input type="text" placeholder = {this.props.auth.address} style={{width: "400px"}} s/>
                </p1>
                <p2>
                  <button onClick={this.onSetAddress}>Set Address</button>
                </p2>
              </form>
              <button
                style={{
                  width: "150px",
                  borderRadius: "3px",
                  letterSpacing: "1.5px",
                  marginTop: "1rem"
                }}
                onClick={this.onLogoutClick}
                className="btn btn-large waves-effect waves-light hoverable blue accent-3"
              >
                Logout
              </button>

            </Navigation>

          </Drawer>
          <Content>
          <div />
          <Switch>
            <PrivateRoute path="/dashboard/home" component ={HomePage} />
            <PrivateRoute path="/dashboard/infopage" component ={InfoPage} />
          </Switch>
          </Content>
        </Layout>
      </div>
    );
  }
}

Main.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps,
  { logoutUser }
)(Main);
